                new DetailComic() { Id = 19, ComicId = 19, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Theo chân Sung JinWoo trên hành trình từ 'thợ săn kém cỏi' đến 'thợ săn hạng S mạnh nhất thế giới'. (có 1 tí Saitama với 1 tí The gamer, bạn nào thích 2 bộ này xin mời nhảy hố=)", SeoDescription = "", SeoTitle = "", SeoAlias = "/toi-thang-cap-mot-minh-471159" },
                new DetailComic() { Id = 20, ComicId = 20, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Astar và Yuno là hai đứa trẻ bị bỏ rơi ở nhà thờ và cùng nhau lớn lên tại đó. Khi còn nhỏ, chúng đã hứa với nhau xem ai sẽ trở thành Ma pháp vương tiếp theo. Thế nhưng, khi cả hai lớn lên, mọi sô chuyện đã thay đổi. Yuno là thiên tài ma pháp với sức mạnh tuyệt đỉnh trong khi Aster lại không thể sử dụng ma pháp và cố gắng bù đắp bằng thể lực. Khi cả hai được nhận sách phép vào tuổi 15, Yuno đã được ban cuốn sách phép cỏ bốn bá (trong khi đa số là cỏ ba lá) mà Aster lại không có cuốn nào. Tuy nhiên, khi Yuno bị đe dọa, sự thật về sức mạnh của Aster đã được giải mã- cậu ta được ban cuốn sách phép cỏ năm lá, cuốn sách phá ma thuật màu đen. Bấy giờ, hai người bạn trẻ đang hướng ra thế giới, cùng chung mục tiêu", SeoDescription = "", SeoTitle = "", SeoAlias = "/phap-su-khong-phep-thuat-937091" },
                new DetailComic() { Id = 21, ComicId = 21, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Câu chuyện kể vể Toriko một thợ săn pro thường xuyên cung cấp các loại thực phẩm quý giá cho nhà hàng và những người giàu có, một anh chàng có đầy sức mạnh để săn bắt các loài động vật hung dữ . Đây là một bộ manga mới phát hành 1 năm rưỡi nhưng đang đứng thứ 4 về mức độ yêu thích của các độc giả Weeky Jump (sau One Piece Naturo Bleach) , mặc dù vậy nhưng Toriko chưa được scan (lậu) rộng rãi.", SeoDescription = "", SeoTitle = "", SeoAlias = "/tho-san-am-thuc-821174" },
                new DetailComic() { Id = 22, ComicId = 22, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Slam Dunk nói về đội bóng rổ của một trường cấp 3 mang tên Shohoku. Nhân vật chính của bộ manga này là Hanamichi Sakuragi, một kẻ giữ kỉ lục 'T bình phương' (thất tình), có sức mạnh vượt trội và rất nóng máu, đứng đầu một nhóm học sinh chuyên quậy phá. Một lần tình cờ, cậu ta quen được Haruko Akagi - cô gái trong mộng và cậu cảm thấy hạnh phúc cực kì khi cô ấy không sợ cậu như những cô gái khác. Haruko người đã nhận ra khả năng tiềm ẩn của Hanamichi, đã giới thiệu cậu vào đội bóng rổ trường Shohoku. Hanamichi lúc đầu cũng không khoái môn bóng rổ cho lắm vì cậu chưa từng chơi bất kì môn thể thao nào trước đó. Thêm nữa, Hanamichi cho rằng bóng rổ là môn thể thao dành cho kẻ thua cuộc (cô gái thứ 15 mà Hanamichi tỏ tình đã từ chối cậu vì cô ấy để ý đến một anh chàng ở đội bóng rổ). Hanamichi, bỏ qua bản tính thiếu kiên trì và hay nóng máu của mình, đã tham gia vào đội bóng rổ để gây sự chú ý của Haruko và cải thiện hình ảnh tệ hại về bản thân ở cô. Kaede Rukawa - đối thủ cứng đầu của Hanamichi (cả trong bóng rổ cũng như trong truyện tình cảm, mặc dù Rukawa không biết rằng Haruko đang để ý hắn), là một ngôi sao nổi tiếng và cực kì... sát gái, cũng gia nhập cùng lúc đó. Hisashi Mitsui, cựu MVP khối cấp hai, và Ryota Miyagi, một cầu thủ 'lùn mà lẹ', cùng lúc trở lại đội bóng trở thành một bộ tứ cùng với đội trưởng Takenori Akagi với ước mơ đưa Shohoku tới giải quốc gia. Cũng lúc đó, các đội bóng rổ bắt đầu để ý đến đội bóng 'toàn sao' Shohoku. Slam Dunk là một trong những bộ manga thể thao mang tính hiện thực. Các nhân vật không phải là những người có tài năng sẵn có, không cần phải cố gắng, và Shokoku không phải luôn luôn chiến thắng. Sự trưởng thành của Hanamichi khá đặc biệt, hắn trưởng thành từ từ và cố gắng hết sức để đạt được thành quả. Slam Dunk đã được TVM Comics chính thức mua bản quyền xuất bản tại Việt Nam vào năm 2009 với tập 1 ra mắt vào tháng 2 năm 2009. NXB tại Nhật: Shueisha.", SeoDescription = "", SeoTitle = "", SeoAlias = "/cao-thu-bong-ro-303271" },
