                new DetailComic() { Id = 32, ComicId = 32, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Các vị thần mở hội nghị để bàn về sự sống còn của nhân loại. Tất cả đều quyết định 'kết liễu' con người, chỉ duy một valkyrie đứng lên phản đối bằng cách đề nghị 1 cuộc chiến giữa thần và người. Cuộc chiến quyết định số phận của nhân loại này sẽ ra sao? Xin mời đón đọc.", SeoDescription = "", SeoTitle = "", SeoAlias = "/shuumatsu-no-valkyrie-907778" },
                new DetailComic() { Id = 33, ComicId = 33, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Đôrêmon là một chú mèo máy được Nôbita, cháu ba đời của Nôbita gửi về quá khứ cho ông mình để giúp đỡ Nôbita tiến bộ, tức là cũng sẽ cải thiện hoàn cảnh của con cháu Nôbita sau này. Còn ở hiện tại, Nôbita là một cậu bé luôn thất bại ở trường học, và sau đó là thất bại trong công việc, đẩy gia đình và con cháu sau này vào cảnh nợ nần. Các câu chuyện trong Đôrêmon thường có một công thức chung, đó là xoay quanh những rắc rối hay xảy ra với cậu bé Nôbita lớp bốn, nhân vật chính thứ nhì của bộ truyện. Đôrêmon có một chiếc túi thần kỳ trước bụng với đủ loại bảo bối của tương lai. Cốt truyện thường gặp nhất sẽ là Nôbita trở về nhà khóc lóc với những rắc rối mà cậu gặp phải ở trường hoặc với bạn bè. Sau khi bị cậu bé van nài hoặc thúc giục, Đôrêmon sẽ đưa ra một bảo bối giúp Nôbita giải quyết những rắc rối của mình, hoặc là để trả đũa hay khoe khoang với bạn bè của cậu. Nôbita sẽ lại thường đi quá xa so với dự định ban đầu của Đôrêmon, thậm chí với những bảo bối mới cậu còn gặp rắc rối lớn hơn trước đó. Đôi khi những người bạn của Nôbita (thường là Xêkô hoặc Chaien) lại lấy trộm những bảo bối và sử dụng chúng không đúng mục đích. Tuy nhiên thường thì ở cuối mỗi câu chuyện, những ai sử dụng sai mục đích bảo bối sẽ phải chịu hậu quả do mình gây ra, và người đọc sẽ rút ra được bài học đạo đức từ đó.", SeoDescription = "", SeoTitle = "", SeoAlias = "/doraemon-501945" },
                new DetailComic() { Id = 34, ComicId = 34, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Hibino Kafka một thanh niên vốn bất mãn với việc làm tại công ty vệ sinh chịu trách nhiệm xử lí xác kaiju. Sau khi bị thương bởi một trận tấn công bất ngờ lại bỗng nhiên hóa thành kaiju! Dưới hình dạng mới, ước mơ và lời hứa khi xưa với cô bạn thuở nhỏ đã có thể thực hiện được", SeoDescription = "", SeoTitle = "", SeoAlias = "/hom-nay-toi-hoa-kaiju-492520" },
                new DetailComic() { Id = 35, ComicId = 35, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Yuuji Itadori là một thiên tài có tốc độ và sức mạnh, nhưng cậu ấy muốn dành thời gian của mình trong Câu lạc bộ Tâm Linh. Một ngày sau cái chết của ông mình, anh gặp Megumi Fushiguro, người đang tìm kiếm vật thể bị nguyền rủa mà các thành viên CLB đã tìm thấy.<br>Đối mặt với những con quái vật khủng khiếp bị 'Ám', Yuuji nuốt vật thể bị phong ấn để có được sức mạnh của nó và cứu bạn bè của mình! Nhưng giờ Yuuji là người bị 'Ám', và cậu ấy sẽ bị kéo vào thế giới ma quỷ ly kỳ của Megumi và những con quái vật độc ác ...", SeoDescription = "", SeoTitle = "", SeoAlias = "/chu-thuat-hoi-chien-500585" },
                new DetailComic() { Id = 36, ComicId = 36, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Một thế giới giả tưởng với các thợ săn lùng mọi cảm giác. Gon, nhân vật chính của truyện, sau khi biết cha mình vẫn còn sống và là một hunter tài năng, đã quyết tâm trở thành một thợ săn. Trong cuộc thi tuyển chọn hunter, Gon đã gặp và kết bạn với Killua, Kurapika và Leorio. Câu truyện lôi cuốn người đọc bởi những nhân vật ấn tượng.", SeoDescription = "", SeoTitle = "", SeoAlias = "/the-gioi-tho-san-664370" },
                new DetailComic() { Id = 37, ComicId = 37, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Hơn 100 năm trước, giống người khổng lồ Titan đã tấn công và đẩy loài người tới bờ vực tuyệt chủng. Những con người sống sót tụ tập lại, xây bao quanh mình 1 tòa thành 3 lớp kiên cố và tự nhốt mình bên trong để trốn tránh những cuộc tấn công của người khổng lồ. Họ tìm mọi cách để tiêu diệt người khổng lồ nhưng không thành công. Và sau 1 thế kỉ hòa bình, giống khổng lồ đã xuất hiện trở lại, một lần nữa đe dọa sự tồn vong của con người....<br>Elen và cô em gái Mikasa phải chứng kiến một cảnh tượng cực kinh khủng - mẹ của mình bị ăn thịt ngay trước mắt. Elen thề rằng cậu sẽ giết tất cả những tên khổng lồ mà cậu gặp...", SeoDescription = "", SeoTitle = "", SeoAlias = "/dai-chien-titan-400872" },
                new DetailComic() { Id = 38, ComicId = 38, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Truyện kể về Ken - một chàng trai Nhật Bản yêu một cô gái Hàn Quốc tên Yumin.<br>Vì tiếng gọi của tình yêu, anh đã theo cô sang Hàn và muốn trở thành cảnh sát như Yumin.<br>Nhưng Ken đã bị cuốn vào vòng xoáy của xã hội đen với hành trình tranh giành địa bàn vô cùng khốc liệt...<br>Máu - Nước mắt - Niềm tin vào chính nghĩa... là những gì bạn sẽ tìm thấy khi đọc Sun-Ken Rock.", SeoDescription = "", SeoTitle = "", SeoAlias = "/sun-ken-rock-715018" },
                new DetailComic() { Id = 39, ComicId = 39, StatusId = 1, CountFollow = 0, CountRating = 0, Rating = 0, Description = "Trong thế giới của Shaman King có những shaman (pháp sư), là các phù thủy có thể điều khiển được các linh hồn. Mỗi pháp sư có một linh hồn đi kèm để hỗ trợ. Cứ 500 năm 1 lần sẽ có một đại hội thi đấu giữa các pháp sư để chọn ra một vua pháp sư. Người này sẽ có được linh hồn vĩ đại của Trái Đất để giúp thế giới. Tuy nhiên 2 cuộc thi đấu gần đây nhất đã bị phá hoại bởi một pháp sư tên là Asakura Hao. Manta là một cậu bé đang học trung học ở Tokyo. Cậu tình cờ làm quen với Yoh, một pháp sư mới chuyển đến cùng lớp. Manta lần lượt gặp những pháp sư khác như Anna, Ryu, Tao Ren, Tao Jun. Shaman King kể về cuộc phiêu lưu của Yoh, Anna, và các bạn qua lời kể của Manta.", SeoDescription = "", SeoTitle = "", SeoAlias = "/vua-phap-thuat-997895" },
